  public void testIsCallable() {
    assertEquals(false, SqlFunc.isCallalbe(null));
    assertEquals(false, SqlFunc.isCallalbe(" "));
    assertEquals(false, SqlFunc.isCallalbe(""));
    assertEquals(true, SqlFunc.isCallalbe("{call procedurename}"));
    assertEquals(true, SqlFunc.isCallalbe("{call prdd}"));
    assertEquals(true, SqlFunc.isCallalbe(" { call procedurename } "));
    assertEquals(true, SqlFunc.isCallalbe("{ Call procedurename }"));
    assertEquals(true, SqlFunc.isCallalbe("   {    Call   procedurename   }   "));
    assertEquals(false, SqlFunc.isCallalbe("   {    Call   procedurename   }   a"));
    assertEquals(false, SqlFunc.isCallalbe("  d {    Call   procedurename   }   "));
    assertEquals(false, SqlFunc.isCallalbe("   { c   Call   procedurename   }   "));
  }

  public void test_isInsertInto() {
    this.assertEquals(false, SqlFunc.isInsertInto(null));
    this.assertEquals(false, SqlFunc.isInsertInto(" "));
    this.assertEquals(false, SqlFunc.isInsertInto(""));
    this.assertEquals(true, SqlFunc.isInsertInto("insert into a"));
    this.assertEquals(true, SqlFunc.isInsertInto("insErt inTo a(ab,c,b) values(ad,e,?)"));
    this.assertEquals(true, SqlFunc.isInsertInto("\r\n insert\r\n into\r\na(ab,c,b)\r\nvalues(ad,e,?)"));
    this.assertEquals(true, SqlFunc.isInsertInto("  insert  into  a(ab,c,b)  values(ad, e,?) "));
    this.assertEquals(false, SqlFunc.isInsertInto("abc"));
    this.assertEquals(false, SqlFunc.isInsertInto("insertinto a"));
    this.assertEquals(false, SqlFunc.isInsertInto("insertainto a"));
    this.assertEquals(false, SqlFunc.isInsertInto("insertainto a"));
  }

  public void test_isSelect() {
    this.assertEquals(false, SqlFunc.isSelect(null));
    this.assertEquals(false, SqlFunc.isSelect(" "));
    this.assertEquals(false, SqlFunc.isSelect(""));
    this.assertEquals(true, SqlFunc.isSelect("select * from a"));
    this.assertEquals(true, SqlFunc.isSelect("select * from( select * from )"));
    this.assertEquals(true, SqlFunc.isSelect(" \r\n select \r\n*\r\nfrom\r\na"));
    this.assertEquals(false, SqlFunc.isSelect("abc"));
    this.assertEquals(false, SqlFunc.isSelect("\r\nselectfrom a"));
    this.assertEquals(false, SqlFunc.isSelect("selectafromb a"));
    this.assertEquals(true, SqlFunc.isSelect(" select ab,cd,e \r\n from \r\na"));
    this.assertEquals(true, SqlFunc.isSelect(" sElect ab,cd,e \r\n frOm \r\na"));

    this
        .assertEquals(
            true,
            SqlFunc
                .isSelect("select bbq_,userid_,btype_,username_,substring(USERID_,1,2) as jgdm, substring(USERID_,8,18) as qydm from NDQGSSDC_XXB1 where BBQ_=? and (upid0=? or upid1=? or upid2=? or upid3=? or upid4=? or upid5=? or upid6=? or upid7=? or upid8=? or upid9=?) and substring(USERID_,8,18) in \r\n(select t1.comp from\r\n(select substring(USERID_,1,2) as taxid, substring(USERID_,8,18) as comp from NDQGSSDC_XXB1 where bbq_=? group by substring(USERID_,1,2), substring(USERID_,8,18)) t1\r\n group by t1.comp having count(t1.comp) > 1) "));

  }

  public void test_isCreate() {
    this.assertEquals(false, SqlFunc.isCreate(null));
    this.assertEquals(false, SqlFunc.isCreate(""));
    this.assertEquals(false, SqlFunc.isCreate(" "));
    this.assertEquals(false, SqlFunc.isCreate("abc"));
    this.assertEquals(false, SqlFunc.isCreate(" \r\n create"));
    this.assertEquals(true, SqlFunc.isCreate(" \r\n create "));
    this.assertEquals(true, SqlFunc.isCreate("creATe "));
    this.assertEquals(true, SqlFunc.isCreate(" creATe table abc "));

    //物理表
    this
        .assertEquals(
            true,
            SqlFunc
                .isCreate(" creATe table \r\n\t $a_bc \r\n as select * from sdfsdf where (t.abc='你好')and(asdfsd) \r\n group by t.sdf "));
    //视图
    this
        .assertEquals(
            true,
            SqlFunc
                .isCreate(" creATe or replace viEw  \r\n\t $a_bc \r\n as select * from sdfsdf where (t.abc='你好')and(asdfsd) \r\n group by t.sdf "));
    //同义词
    this.assertEquals(true, SqlFunc.isCreate(" creATe public synonym id for Vname"));
  }

  public void test_isUpdate() {
    this.assertEquals(false, SqlFunc.isUpdate(null));
    this.assertEquals(false, SqlFunc.isUpdate(" "));
    this.assertEquals(false, SqlFunc.isUpdate(""));
    this.assertEquals(true, SqlFunc.isUpdate("update a set a=b"));
    this.assertEquals(true, SqlFunc.isUpdate("\r\n update \r\n a \r\n set \r\na=b"));
    this.assertEquals(false, SqlFunc.isUpdate("abc"));
    this.assertEquals(false, SqlFunc.isUpdate("updateset a"));
    this.assertEquals(false, SqlFunc.isUpdate("updateabcsetdef a"));
  }

  public void test_isValidSymbol() {
    this.assertEquals(false, SqlFunc.isValidSymbol(null));
    this.assertEquals(false, SqlFunc.isValidSymbol(" "));
    this.assertEquals(false, SqlFunc.isValidSymbol(""));
    this.assertEquals(true, SqlFunc.isValidSymbol("a"));
    this.assertEquals(true, SqlFunc.isValidSymbol("a_b0$"));
    this.assertEquals(false, SqlFunc.isValidSymbol("1a$bc"));
    this.assertEquals(false, SqlFunc.isValidSymbol("updateset "));
    this.assertEquals(false, SqlFunc.isValidSymbol(" updateabcsetdef"));
    
    this.assertEquals(true, SqlFunc.isValidSymbol("dbo.tablename"));
    this.assertEquals(true, SqlFunc.isValidSymbol("dbo.table.name"));
    this.assertEquals(false, SqlFunc.isValidSymbol(".table.name"));
    this.assertEquals(false, SqlFunc.isValidSymbol("dboname."));
  }

