  public void test_getTopPageSQL() {
    String sql = "select * from dbname";
    assertEquals("select top 10 * from dbname", DialectImpl.getTopPageSQl(sql, 10));

    sql = " select  distinct id from dbname";
    assertEquals("select distinct top 10 id from dbname", DialectImpl.getTopPageSQl(sql, 10));
    sql = " seLect  distInct id from dbname";
    assertEquals("select distInct top 10 id from dbname", DialectImpl.getTopPageSQl(sql, 10));
  }

  public void test_getTablename() {
    assertEquals("AtBname", SqlFunc.getTablename("select * from AtBname "));
    assertEquals("AtBname", SqlFunc.getTablename("  select *  from  AtBname"));
    assertEquals(null, SqlFunc.getTablename(" seLect  *  froM  AtBname7 where a>0"));
    assertEquals(null, SqlFunc.getTablename("(select * from AtBname )"));
    assertEquals(null, SqlFunc.getTablename("select * from 5AtBname  "));
    assertEquals(null, SqlFunc.getTablename("select field1,feild2 from AtBname  "));
    
    assertEquals("dbo.AtBname", SqlFunc.getTablename("select * from dbo.AtBname "));
    assertEquals("dbo.schema.AtBname", SqlFunc.getTablename("select * from dbo.schema.AtBname "));
  }

  /**
   * jdbc:postgresql://127.0.0.1:5432/orcdb
   * jdbc:sybase:Tds:127.0.0.1:2638/orcdb
   * jdbc:informix-sqli://127.0.0.1:port/orcdb:informixserver=SERVERNAME
   * jdbc:db2://127.0.0.1:50000/orcdb
   * jdbc:jtds:sqlserver://127.0.0.1:1433/orcdb;TDS=8.0;SendStringParametersAsUnicode=true
   * jdbc:mysql://127.0.0.1/orcdb
   * jdbc:oracle:thin:@127.0.0.1:1521:orcdb
   */
  public void test_getIpAddressFromUrl() {
    assertEquals("127.0.0.1", SqlFunc.getIpAddressFromUrl("jdbc:postgresql://127.0.0.1:5432/orcdb"));
    assertEquals("127.0.0.1", SqlFunc.getIpAddressFromUrl("jdbc:sybase:Tds:127.0.0.1:2638/orcdb"));
    assertEquals("127.34.0.1", SqlFunc
        .getIpAddressFromUrl("jdbc:informix-sqli://127.34.0.1:port/orcdb:informixserver=SERVERNAME"));
    assertEquals("127.0.0.1", SqlFunc.getIpAddressFromUrl("jdbc:db2://127.0.0.1:50000/orcdb"));
    assertEquals("127.0.0.123", SqlFunc
        .getIpAddressFromUrl("jdbc:jtds:sqlserver://127.0.0.123:1433/orcdb;TDS=8.0;SendStringParametersAsUnicode=true"));
    assertEquals("127.0.0.1", SqlFunc.getIpAddressFromUrl("jdbc:oracle:thin:@127.0.0.1:1521:orcdb"));
    assertEquals("127.0.0.1", SqlFunc.getIpAddressFromUrl("jdbc:mysql://127.0.0.1/orcdb"));

    assertEquals("localhost", SqlFunc.getIpAddressFromUrl("jdbc:postgresql://localhost:5432/orcdb"));
    assertEquals("localhost", SqlFunc.getIpAddressFromUrl("jdbc:postgresql://localhost/orcdb"));
    assertEquals("ab_d-ds", SqlFunc.getIpAddressFromUrl("jdbc:sybase:Tds:ab_d-ds:2638/orcdb"));
    assertEquals("ab_d-ds", SqlFunc.getIpAddressFromUrl("jdbc:sybase:Tds:ab_d-ds/orcdb"));

    assertEquals("ab_d-ds", SqlFunc
        .getIpAddressFromUrl("jdbc:informix-sqli://ab_d-ds:port/orcdb:informixserver=SERVERNAME"));
    assertEquals("ab_d-ds", SqlFunc.getIpAddressFromUrl("jdbc:informix-sqli://ab_d-ds/orcdb:informixserver=SERVERNAME"));

    assertEquals("asdf34-dsf_34", SqlFunc.getIpAddressFromUrl("jDbc:db2://asdf34-dsf_34:50000/orcdb"));
    assertEquals("asdf34-dsf_34", SqlFunc.getIpAddressFromUrl("jdbc:db2://asdf34-dsf_34/orcdb"));
    assertEquals(
        "asdf34-dsf_34",
        SqlFunc
            .getIpAddressFromUrl("jdbc:jtds:sqlserver://asdf34-dsf_34:1433/orcdb;TDS=8.0;SendStringParametersAsUnicode=true"));
    assertEquals("asdf34-dsf_34", SqlFunc
        .getIpAddressFromUrl("jdbc:jtds:sqlserver://asdf34-dsf_34/orcdb;TDS=8.0;SendStringParametersAsUnicode=true"));
    assertEquals("asdf34-dsf_34", SqlFunc.getIpAddressFromUrl("jdbc:oracle:thin:@asdf34-dsf_34:1521:orcdb"));
    assertEquals("asdf34-dsf_34", SqlFunc.getIpAddressFromUrl("jdbc:oraCle:thin:@asdf34-dsf_34:orcdb"));
    assertEquals("asdf34-dsf_34", SqlFunc.getIpAddressFromUrl("jdbc:mysql://asdf34-dsf_34/orcdb"));
  }
